import React, { useEffect, useState } from 'react';
import './SelectCharacter.css';
import EpicRPS from '../../contracts/contract';
import { MinButton, NFT } from '../NFT';

/*
 * Don't worry about setCharacterNFT just yet, we will talk about it soon!
 */
const SelectCharacter = ({ setCharacterNFT }) => {

  const [defaultCharacters, setDefaultCharacters] = useState(0);
	const [chosenCharacterIndex, setChosenCharacterIndex]  = useState(-1);

  useEffect(() =>{

    setDefaultCharacters(EpicRPS.getDefaultCharacters());
  },[]);

	const selectChar = (i) => {
		setChosenCharacterIndex(i);
	}



  return (
    <div className="select-character-container">
     <h2>Mint Your Hero. Choose wisely.</h2>
     {defaultCharacters && 
		 	<div>{defaultCharacters}</div>
		 }
    </div>
  );
}

export default SelectCharacter;
